class WriteNDListToCSV:
    def __init__(self, filepath, data):
        f = open(filepath, "w")
        content = ""
        for item in data:
            line = ""
            for value in item:
                line += "%s," % value 
            line = "%s\n" % line[:-1]
            content += line
        f.write(content)
        f.close()
